import React, { useState, useEffect } from "react";
import { getFiles } from "../services/fileServices"

const FilePage = () => {
  //States
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [files, setFiles] = useState(null);
  const [search, setSearch] = useState(null);

  useEffect(() => {
    (async () => {
      setLoading(true);
      try {
        const files = await getFiles();
        setLoading(false);
        setFiles(files);
      }
      catch (e) {
        console.log(`[File Page] error fetching files`, e);
        setLoading(false);
        setError(e);
      }
    })();
  }, []);

  const onChange = (e) => {
    setSearch(e.target.value);
  };

  if (error) {
    return (
      <div class="text-center">
        {errors}
      </div>
    )
  }
  return (
    <div className="p-5">
      <input
        type="string"
        id="search"
        onChange={onChange}
        required
      />
      <table class="table table-bordered">
        <thead>
          <tr>
            <th scope="col">File Name</th>
            <th scope="col">Text</th>
            <th scope="col">Number</th>
            <th scope="col">Hex</th>
          </tr>
        </thead>
        <tbody>
          {loading && <div class="d-flex justify-content-center w-full position-absolute top-50 start-50">
            <div class="spinner-border" role="status">
            </div>
          </div>
          }
          {
            files?.map((file => {
              return file?.lines.map((line, index) => {
                return (
                  <tr key={index}>
                    <th scope="row">{file?.file}</th>
                    <th scope="row">{line?.text}</th>
                    <th scope="row">{line?.number}</th>
                    <th scope="row">{line?.hex}</th>
                  </tr>
                )
              })
            }))
          };
        </tbody>
      </table>
    </div>
  )

}

export default FilePage;
